import React, { Component } from 'react';
import AddCard from './forms/addCard';
import { Tooltip } from '@material-ui/core';

class ProjTooltip extends Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            tooltipOpen: false
        };
    }

    toggle() {
        this.setState({
            tooltipOpen: !this.state.tooltipOpen
        });
    }
    render() {
        return (
            <span>
                <i className="questionCircle" id={'Tooltip-' + this.props.id} data-toggle="tooltip"></i>
                <Tooltip placement={this.props.placement} isOpen={this.state.tooltipOpen} target={'Tooltip-' + this.props.id} toggle={this.toggle}>
                    {this.props.content}
                </Tooltip>

                <AddCard listType={this.props.listType} status={this.props.id} />
            </span>
        );
    }


}
export default ProjTooltip;
