    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            title: '',
            createdBy: '',
            count: 2
        };

        //this.toggle = this.toggle.bind(this);
    }
    handleChange = event => {
        this.setState({ name: event.target.value });
    }
    handleInput(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state.dueDate)
    }
