    render() {

        return (
            <div>
                <Button variant="contained" color="default" onClick={this.toggle}><Add />New Project</Button>
                <Dialog open={this.state.open} className={this.props.className} >
                    <DialogTitle>
                        Add List
                    </DialogTitle>
                    <ModalBody>
                        <FormGroup><Label for="title">List Title(*):</Label><Input type="text" name="title" onChange={this.handleInput.bind(this)} /></FormGroup>
                        <FormGroup><Label for="createdBy">Created by(*):</Label><Input type="text" name="createdBy" onChange={this.handleInput.bind(this)} /></FormGroup>

                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary">Add</Button>
                        <Button color="secondary">Close</Button>

                        {/* <Button color="primary" onClick={this.handleClick.bind(this)}><i className="btnPlusCircle"></i> Add</Button>
                        <Button color="secondary" onClick={this.toggle}><i className="btnCloseCircl"></i> Close</Button> */}
                    </ModalFooter>

                </Dialog>
            </div>
        );
    }



}
export default AddList;
