import React, { Component } from 'react';
import AddCard from './forms/addCard';
import { Tooltip } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import HelpOutline from '@material-ui/icons/HelpOutline'

class ProjTooltip extends Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            tooltipOpen: false
        };
    }

    toggle() {
        this.setState({
            tooltipOpen: !this.state.tooltipOpen
        });
    }
    render() {
        return (
            <span>

                <i className="questionCircle" id={'Tooltip-' + this.props.id} data-toggle="tooltip"></i>
                <Tooltip placement={this.props.placement} open={this.state.tooltipOpen} toggle={this.toggle}>
                    <IconButton color="default" size="small" id={'Tooltip-' + this.props.id} data-toggle="tooltip">
                        <HelpOutline />
                    </IconButton>
                    {this.props.content}
                </Tooltip>

                <AddCard listType={this.props.listType} status={this.props.id} />
            </span>
        );
    }


}
export default ProjTooltip;
