import React from 'react';
import { Input } from 'reactstrap';
import { Add, Close, CalendarToday, Schedule, DoubleArrow, Update } from '@material-ui/icons';
import { makeStyles } from '@material-ui/core/styles';
import { Button, IconButton, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, Slide, FormGroup, FormLabel, RadioGroup, Radio, FormControlLabel, TextField } from '@material-ui/core';
//import moment from 'moment'
//import axios from 'axios'

class ModalProject extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            title: this.props.propContent.title,
            content: this.props.propContent.content,
            status: this.props.propContent.status,
            color: this.props.propContent.color
        };

        this.toggle = this.toggle.bind(this);
    }
    handleInput(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state.title)
    }
    // handleClick = id => {
    //     axios.put(`/cards/update/${id}`, {
    //         title: this.state.title,
    //         content: this.state.content,
    //         status: this.state.status
    //     })
    //         .then((response) => {
    //             if (response.data.message)
    //                 alert(response.data.message)
    //             else {
    //                 this.toggle();
    //                 this.setState({
    //                     title: null,
    //                     content: null,
    //                     loading: false
    //                 })
    //             }
    //             console.log(response);
    //         })
    //         .catch((error) => {
    //             console.log(error);
    //         });

    // }
    // toggle() {
    //     this.setState({
    //         modal: !this.state.modal
    //     });
    // }
    render() {
        let { title, content, status } = this.state;
        const { propContent, classType } = this.props;
        return (
            <div>
                <IconButton size="small" className={classType} variant="contained" color="default" /*onClick={this.toggle}*/><DoubleArrow /></IconButton>
                <Dialog keepMounted open={this.state.modal} toggle={this.toggle} className={this.props.className} >
                    <DialogTitle toggle={this.toggle} >
                        <FormLabel component="title">Card Title:</FormLabel>
                        <TextField name="title" value={title} onChange={this.handleInput.bind(this)} />
                    </DialogTitle>
                    <DialogContent>
                        <FormGroup>
                            <FormLabel conponent="content">Card Details:</FormLabel>
                            <TextField name="content" value={content} onChange={this.handleInput.bind(this)} />
                        </FormGroup>
                        <FormLabel conponent="status">Status:</FormLabel>
                        <FormControl component="fieldset">
                            <FormLabel component="color">Card Color:</FormLabel>
                            <RadioGroup value={status} aria-label="status" name="status" id="status" onChange={this.handleInput.bind(this)}>
                                <FormControlLabel value="1" control={<StyledRadio />} label="Backlog" />
                                <FormControlLabel value="2" control={<StyledRadio />} label="TODO" />
                                <FormControlLabel value="3" control={<StyledRadio />} label="In Progress" />
                                <FormControlLabel value="4" control={<StyledRadio />} label="Testing" />
                                <FormControlLabel value="5" control={<StyledRadio />} label="Completed" />

                            </RadioGroup>
                        </FormControl>
                        <Input type="select" value={status} name="status" id="status" onChange={this.handleInput.bind(this)}>
                            <option value="1">Backlog</option>
                            <option value="2">Todo</option>
                            <option value="3">In Progress</option>
                            <option value="4">Testing</option>
                            <option value="5">Completed</option>
                        </Input>
                        <hr />
                        {/* <i className="cardCalendar"></i> Created Date: {moment(propContent.date).format("DD.MM.YYYY")}<br />
                        <i className="cardClock"></i> Due Date: {moment(propContent.dueDate).format("DD.MM.YYYY")}<br /> */}
                        <i className="boardUser"></i> Created by: {propContent.createdBy}
                    </DialogContent>
                    <DialogActions>
                        <img height="35" alt={propContent.contributors[0].name + ' ' + propContent.contributors[0].lastName} title={propContent.contributors[0].name + ' ' + propContent.contributors[0].lastName} src={'../../public/assets/img' + propContent.contributors[0].profilePhoto} />
                        <Button variant="contained" color="default" onClick={() => this.handleClick(propContent._id)}><Update />Update</Button>
                        <Button variant="contained" color="default" onClick={this.toggle}><Close />Close</Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }

}
export default ModalProject;
