import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Input, FormGroup, Label } from 'reactstrap';
//import moment from 'moment'
//import axios from 'axios'


class AddCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            title: '',
            content: '',
            contributors: '',
            createdBy: 'Yasmin Afifi',
            dueDate: '',
            status: this.props.status,
            color: '',
            listId: this.props.listType,
            loading: false,
            users: []
        };
        this.toggle = this.toggle.bind(this);
    }
    // componentDidMount() {
    //     moment.locale('tr');
    //     this.changeColumnTitle()
    // }
    changeColumnTitle = number => {
        let newTitle;
        if (number === "1")
            newTitle = "Backlog"
        else if (number === "2")
            newTitle = "ToDo"
        else if (number === "3")
            newTitle = "In Progress"
        else if (number == "4")
            newTitle = "Testing"
        else
            newTitle = "Completed"

        return newTitle;
    }
    handleInput(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state.dueDate)
    }
    // getUsers() {
    //     axios.get('/users')
    //         .then((r) => {
    //             this.setState({
    //                 users: r.data,
    //                 err: ''
    //             })
    //         })
    //         .then((r) => {
    //             console.log(this.state.users)
    //         })
    //         .catch((e) => {
    //             this.setState({
    //                 err: e
    //             })
    //         })
    // }
    // handleClick = event => {
    //     axios.post('/cards', {
    //         title: this.state.title,
    //         content: this.state.content,
    //         status: this.props.status,
    //         contributors: this.state.contributors,
    //         dueDate: this.state.dueDate,
    //         color: this.state.color,
    //         listId: this.state.listId,
    //         createdBy: this.state.createdBy
    //     })
    //         .then((response) => {
    //             if (response.data.message)
    //                 alert(response.data.message)
    //             else {
    //                 this.toggle();
    //                 this.setState({
    //                     title: null,
    //                     content: null,
    //                     contributors: null,
    //                     dueDate: null,
    //                     loading: false
    //                 })
    //             }
    //             console.log(response);
    //         })
    //         .catch((error) => {
    //             console.log(error);
    //         });

    // }
    // toggle() {
    //     this.getUsers();
    //     this.setState({
    //         modal: !this.state.modal
    //     });
    // }
    render() {
        // const { users } = this.state;
        let userContent;
        // if (!users)
        //     userContent = <option value="">Loading...</option>
        // else {
        //     userContent = users.map((user, index) => (
        //         <option key={index} value={user._id}>{user.name + " " + user.lastName}</option>
        //     ))
        // }
        return (
            <div>
                <i className="customAddCard" onClick={this.toggle}></i>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                    <ModalHeader toggle={this.toggle}>
                        Create a New Card to {this.changeColumnTitle(this.props.status)}
                    </ModalHeader>
                    <ModalBody>
                        <FormGroup>
                            <Label for="title">Card Title(*):</Label><Input type="text" name="title" id="CardTitle" onChange={this.handleInput.bind(this)} /></FormGroup>
                        <FormGroup>
                            <Label for="content">Card Details:</Label>
                            <Input type="textarea" name="content" id="content" onChange={this.handleInput.bind(this)} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="contributors">Assign to:</Label>
                            <Input type="select" name="contributors" id="contributors" onChange={this.handleInput.bind(this)}>
                                <option value="">Choose:</option>
                                {userContent}
                            </Input>
                        </FormGroup>
                        <FormGroup>
                            <Label for="color">Card Color:</Label>
                            <Input type="select" name="color" id="color" onChange={this.handleInput.bind(this)}>
                                <option value="">Choose:</option>
                                <option value="colorBlue">Blue</option>
                                <option value="colorGreen">Green</option>
                                <option value="colorYellow">Yellow</option>
                                <option value="colorRed">Red</option>
                            </Input>
                        </FormGroup>
                        <hr />
                        {/* <i className="cardCalendar"></i> Created Date: {moment().format('L, h:mm:ss')} <br /> */}
                        <i className="cardClock"></i> Due Date: <input name="dueDate" id="dueDate" type="datetime-local" onChange={this.handleInput.bind(this)} />
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary">Add</Button>
                        <Button color="secondary">Close</Button>
                        {/* <Button color="primary" onClick={this.handleClick.bind(this)}><i className="btnPlusCircle"></i> Add</Button>
                        <Button color="secondary" onClick={this.toggle}><i className="btnCloseCircle"></i> Close</Button> */}
                    </ModalFooter>
                </Modal>
            </div>
        );
    }

}
export default AddCard;
