    //                     err: e
    //                 })
    //             }
    //             else
    //                 this.setState({
    //                     loading: false,
    //                     err: e
    //                 })
    //         })


    // }
    render() {
        let { lists, loadingList } = this.state;
        let listTable;
        if (!loadingList)
            listTable = lists.map((list, index) => {
                return (
                    <li key={index}>
                        {/* <Link to={`/list/${list.listId}`} activeClassName="active">
                            <i className="fas fa-list-alt"></i>
                            <span className="menu-text">{list.title}</span>
                        </Link> */}
                    </li>
                )
            })
        else
            listTable = <li>
                <div className="loader">
                    <ProjLoader />
                </div>
            </li>
        return (
            <div>
                <div className="side">
                    <span className="logo">Project Board</span>
                    <ul className="side-menu">
                        {listTable}
                    </ul>
                    <div className="otherMenu">
                        <AddList />
                    </div>
                </div>
                <div className="con">
                    <HeaderApp />
                    <aside>
                        <List listName={this.state.lists.filter(i => i.listId === parseInt(this.props.router.params.id))} listType={this.props.params.id} cards={this.state.cards} loading={this.state.loading} />
                    </aside>

                </div>
            </div>
        )
    }


}
export default Board;
